function closestPolyfill(el, selector) {
    while (el) {
        if (el.matches(selector)) return el;
        el = el.parentElement;
    }
    return null;
}

if (!Element.prototype.closest) {
    Element.prototype.closest = function(selector) {
        let el = this;
        while (el) {
            if (el.matches(selector)) return el;
            el = el.parentElement;
        }
        return null;
    };
}

document.addEventListener('click', (event) => {
    const board = event.target.closest('.Board');
    if (board) {
        const element = closestPolyfill(event.target, 'div');
        if (element) {
            const computedStyle = window.getComputedStyle(element);
            const gridArea = computedStyle.gridArea;
            const rowStart = computedStyle.gridRowStart;
            const columnStart = computedStyle.gridColumnStart;

            if (gridArea !== 'none') {
                console.log(`Grid Area: ${gridArea}`);
            }
            if (rowStart !== 'auto' && columnStart !== 'auto') {
                console.log(`Row: ${rowStart}, Column: ${columnStart}`);
            }
        }
    }
});

let selectedChecker = null; // Выбранная шашка

document.addEventListener("click", (event) => {
    const cell = event.target.closest(".Board");
    const checker = event.target.closest(".checker");

    if (checker) {
        // Выделяем шашку
        if (selectedChecker) selectedChecker.classList.remove("selected");
        selectedChecker = checker;
        selectedChecker.classList.add("selected");
    } else if (cell && selectedChecker) {
        const currentCell = selectedChecker.closest(".Board");
        const targetCell = cell;

        // Проверяем, можно ли переместить шашку
        if (isMoveValid(currentCell, targetCell, selectedChecker)) {
            // Если прыжок через шашку — удаляем её
            const jumpedCell = getJumpedCell(currentCell, targetCell);
            if (jumpedCell) {
                const jumpedChecker = jumpedCell.querySelector(".checker");
                if (jumpedChecker) jumpedChecker.remove();
            }

            // Перемещаем шашку
            targetCell.appendChild(selectedChecker);
            selectedChecker.classList.remove("selected");
            selectedChecker = null;
        }
    }
});

function isMoveValid(currentCell, targetCell, checker) {
    const currentRow = parseInt(currentCell.dataset.row);
    const currentCol = parseInt(currentCell.dataset.col);
    const targetRow = parseInt(targetCell.dataset.row);
    const targetCol = parseInt(targetCell.dataset.col);

    const rowDiff = Math.abs(targetRow - currentRow);
    const colDiff = Math.abs(targetCol - currentCol);

    // Проверяем, что целевая клетка пуста
    if (targetCell.querySelector(".checker")) return false;

    // Проверяем обычный ход
    if (rowDiff === 1 && colDiff === 1) return true;

    // Проверяем прыжок через шашку
    if (rowDiff === 2 && colDiff === 2) {
        const jumpedCell = getJumpedCell(currentCell, targetCell);
        if (jumpedCell && jumpedCell.querySelector(".checker")) {
            return true;
        }
    }

    return false;
}

function getJumpedCell(currentCell, targetCell) {
    const currentRow = parseInt(currentCell.dataset.row);
    const currentCol = parseInt(currentCell.dataset.col);
    const targetRow = parseInt(targetCell.dataset.row);
    const targetCol = parseInt(targetCell.dataset.col);

    const jumpedRow = (currentRow + targetRow) / 2;
    const jumpedCol = (currentCol + targetCol) / 2;

    return document.querySelector(
        `.Cell[gridArea="${jumpedRow} / ${jumpedCol}"]`
        //currentCell.style.gridArea = `${jumpedRow} / ${jumpedCol}`
    );
}
