// Полифил для метода closest
function closestPolyfill(el, selector) {
    while (el) {
        if (el.matches(selector)) return el;
        el = el.parentElement;
    }
    return null;
}

// Проверяем, если метод closest еще не определен в Element
if (!Element.prototype.closest) {
    Element.prototype.closest = function(selector) {
        let el = this;
        while (el) {
            if (el.matches(selector)) return el;
            el = el.parentElement;
        }
        return null;
    };
}

document.addEventListener('click', (event) => {
    const board = event.target.closest('.Board');
    if (board) {
        const element = closestPolyfill(event.target, 'div');
        if (element) {
            const computedStyle = window.getComputedStyle(element);
            const gridArea = computedStyle.gridArea;
            const rowStart = computedStyle.gridRowStart;
            const columnStart = computedStyle.gridColumnStart;

            if (gridArea !== 'none') {
                console.log(`Grid Area: ${gridArea}`);
            }
            if (rowStart !== 'auto' && columnStart !== 'auto') {
                console.log(`Row: ${rowStart}, Column: ${columnStart}`);
            }
        }
    }
});

// Переменная для выбранной шашки
let selectedChecker = null;

document.addEventListener("click", (event) => {
    const cell = event.target.closest(".Cell");
    const checker = event.target.closest(".checker");

    if (checker) {
        // Выделяем шашку
        if (selectedChecker) selectedChecker.classList.remove("selected");
        selectedChecker = checker;
        selectedChecker.classList.add("selected");
    } else if (cell && selectedChecker) {
        const currentCell = selectedChecker.closest(".checker");
        const targetCell = cell;

        console.log("currentCell", currentCell);
        console.log("targetCell", targetCell);

        // Проверяем, можно ли переместить шашку
        if (isMoveValid(currentCell, targetCell, selectedChecker)) {
            console.log("Move is valid");

            // Если прыжок через шашку — удаляем её
            const jumpedCell = getJumpedCell(currentCell, targetCell);
            if (jumpedCell) {
                const jumpedChecker = jumpedCell.querySelector(".checker");
                if (jumpedChecker) {
                    console.log("Jumped Checker Removed");
                    jumpedChecker.remove();
                }
            }

            // Перемещаем шашку
            targetCell.appendChild(selectedChecker);
            selectedChecker.classList.remove("selected");
            selectedChecker = null;

            console.log("Checker moved");
        } else {
            console.log("Move is invalid");
        }
    }
});

function isMoveValid(currentCell, targetCell, checker) {
    console.log("Validating move...");

    // Проверяем, что currentCell и targetCell существуют
    if (!currentCell || !targetCell) {
        console.log("currentCell or targetCell is null");
        return false;
    }

    const currentGridArea = window.getComputedStyle(currentCell).gridArea;
    const targetGridArea = window.getComputedStyle(targetCell).gridArea;

    const [currentRow, currentCol] = parseGridArea(currentGridArea);
    const [targetRow, targetCol] = parseGridArea(targetGridArea);

    const rowDiff = Math.abs(targetRow - currentRow);
    const colDiff = Math.abs(targetCol - currentCol);

    // Проверяем, что целевая клетка пуста
    if (!targetCell.querySelector(".checker")) {
        // Проверяем обычный ход
        if (rowDiff === 1 && colDiff === 1) {
            const jumpedCell = getJumpedCell(currentCell, targetCell);
            if (jumpedCell) {
                console.log("Jumped Cell Found:", jumpedCell);
                const jumpedChecker = jumpedCell.querySelector(".checker");
                if (jumpedChecker) {
                    console.log("Jumped Checker Removed");
                    jumpedChecker.remove();
                }
            }
            return true;
        }

        // Проверяем прыжок через шашку
        if (rowDiff === 2 && colDiff === 2) {
            console.log("Checking for jump...");
            const jumpedCell = getJumpedCell(currentCell, targetCell);
            if (jumpedCell) {
                console.log("Jumped Cell Found:", jumpedCell);
                const jumpedChecker = jumpedCell.querySelector(".checker");
                if (jumpedChecker) {
                    console.log("Jumped Checker Removed");
                    jumpedChecker.remove();
                }
                return true;
            }
        }
    }

    return false;
}

function getJumpedCell(currentCell, targetCell) {
    console.log("getJumpedCell called");

    // Проверяем, что currentCell и targetCell существуют
    if (!currentCell || !targetCell) {
        console.log("currentCell or targetCell is null in getJumpedCell");
        return null;
    }

    const currentGridArea = window.getComputedStyle(currentCell).gridArea;
    const targetGridArea = window.getComputedStyle(targetCell).gridArea;

    const [currentRow, currentCol] = parseGridArea(currentGridArea);
    const [targetRow, targetCol] = parseGridArea(targetGridArea);

    console.log(`currentRow: ${currentRow}, currentCol: ${currentCol}`);
    console.log(`targetRow: ${targetRow}, targetCol: ${targetCol}`);

    // Вычисляем промежуточные координаты
    const jumpedRow = Math.round((currentRow + targetRow) / 2);
    const jumpedCol = Math.round((currentCol + targetCol) / 2);

    console.log(`jumpedRow: ${jumpedRow}, jumpedCol: ${jumpedCol}`);

    // Находим промежуточную ячейку с помощью grid-area
    return findCellByGridArea(jumpedRow, jumpedCol);
}

function parseGridArea(gridArea) {
    const parts = gridArea.split(" / ").map(part => parseInt(part.trim(), 10));
    return [parts[0], parts[1]]; // row-start и col-start
}

function findCellByGridArea(row, col) {
    console.log(`Searching for cell at row=${row}, col=${col}`);
    return Array.from(document.querySelectorAll(".Cell")).find(cell => {
        const gridArea = window.getComputedStyle(cell).gridArea;
        const [cellRow, cellCol] = parseGridArea(gridArea);
        console.log(`Checking cell with gridArea: row=${cellRow}, col=${cellCol}`);
        return cellRow === row && cellCol === col;
    });
}
