$(document).ready(function () {
    let selectedChecker = null;

    // Обработчик клика по шашке
    $(".checker").on("click", function () {
        if (selectedChecker === this) {
            selectedChecker = null;
            $(this).removeClass("selected");
        } else {
            if (selectedChecker) {
                $(selectedChecker).removeClass("selected");
            }
            selectedChecker = this;
            $(this).addClass("selected");
        }
    });

    // Обработчик клика по клетке
    $(".Cell").on("click", function () {
        if (selectedChecker) {
            const selectedCell = $(this);
            const selectedRow = parseInt(selectedCell.data("row"), 10);
            const selectedCol = parseInt(selectedCell.data("col"), 10);

            const checkerRow = parseInt($(selectedChecker).css("grid-area").split(" / ")[0], 10);
            const checkerCol = parseInt($(selectedChecker).css("grid-area").split(" / ")[1], 10);

            const rowDiff = Math.abs(selectedRow - checkerRow);
            const colDiff = Math.abs(selectedCol - checkerCol);

            console.log(`Selected position: ${selectedRow}, ${selectedCol}`);
            console.log(`Checker position: ${checkerRow}, ${checkerCol}`);
            console.log(`Row diff: ${rowDiff}, Col diff: ${colDiff}`);

            if (rowDiff === 2 && colDiff === 2) {
                const middleRow = (selectedRow + checkerRow) / 2;
                const middleCol = (selectedCol + checkerCol) / 2;

                console.log(`Middle position: ${middleRow}, ${middleCol}`);

                const middleCell = $(".Cell").filter(function () {
                    return $(this).data("row") === middleRow && $(this).data("col") === middleCol;
                });

                if (middleCell.length && middleCell.find(".checker").length) {
                    console.log("Middle cell contains checker");
                    const middleChecker = middleCell.find(".checker");
                    const currentCheckerColor = $(selectedChecker).hasClass("black") ? "black" : "white";
                    const middleCheckerColor = middleChecker.hasClass("black") ? "black" : "white";

                    if (currentCheckerColor !== middleCheckerColor) {
                        middleChecker.remove();
                        $(selectedChecker).css("grid-area", `${selectedRow} / ${selectedCol}`);
                        $(selectedChecker).removeClass("selected");
                        selectedChecker = null;
                    }
                } else {
                    console.log("No checker to jump over");
                }
            } else if (rowDiff === 1 && colDiff === 1) {
                $(selectedChecker).css("grid-area", `${selectedRow} / ${selectedCol}`);
                $(selectedChecker).removeClass("selected");
                selectedChecker = null;
            }
        }
    });

});
