import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import Checkers from "./DOM/Checkers";
import reportWebVitals from './reportWebVitals';
import GameModel from "./GameModel/GameModel";

const gameModel = new GameModel();

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.StrictMode>
        <Checkers GameModel={gameModel} />
    </React.StrictMode>
);

function closestPolyfill(el, selector) {
    while (el) {
        if (el.matches(selector)) return el;
        el = el.parentElement;
    }
    return null;
}

if (!Element.prototype.closest) {
    Element.prototype.closest = function(selector) {
        let el = this;
        while (el) {
            if (el.matches(selector)) return el;
            el = el.parentElement;
        }
        return null;
    };
}

document.querySelector('.Board').addEventListener('click', (event) => {
    // Полифилл closest
    const element = closestPolyfill(event.target, 'div');
    if (element) {
        const computedStyle = window.getComputedStyle(element);
        const gridArea = computedStyle.gridArea;
        const rowStart = computedStyle.gridRowStart;
        const columnStart = computedStyle.gridColumnStart;

        if (gridArea !== 'none') {
            console.log(`Grid Area: ${gridArea}`);
        }
        if (rowStart !== 'auto' && columnStart !== 'auto') {
            console.log(`Row: ${rowStart}, Column: ${columnStart}`);
        }
    }
});

reportWebVitals();
