function closestPolyfill(el, selector) {
    while (el) {
        if (el.matches(selector)) return el;
        el = el.parentElement;
    }
    return null;
}

if (!Element.prototype.closest) {
    Element.prototype.closest = function(selector) {
        let el = this;
        while (el) {
            if (el.matches(selector)) return el;
            el = el.parentElement;
        }
        return null;
    };
}

// Добавляем обработчик клика на .Board
const board = document.querySelector('.Board');
if (board) {
    board.addEventListener('click', (event) => {
        const element = closestPolyfill(event.target, 'div'); // Поиск ближайшего div

        if (element) {
            const computedStyle = window.getComputedStyle(element); // Получаем стили элемента
            const gridArea = computedStyle.gridArea;
            const rowStart = computedStyle.gridRowStart;
            const columnStart = computedStyle.gridColumnStart;

            if (gridArea !== 'none') {
                console.log(`Grid Area: ${gridArea}`); // Логируем gridArea
            }
            if (rowStart !== 'auto' && columnStart !== 'auto') {
                console.log(`Row: ${rowStart}, Column: ${columnStart}`); // Логируем начальную строку и столбец
            }
        }
    });
}
