$(document).ready(function () {
    let selectedChecker = null;

    // Обработчик клика по шашке
    $(".checker").on("click", function () {
        if (selectedChecker === this) {
            selectedChecker = null;
            $(this).removeClass("selected");
        } else {
            if (selectedChecker) {
                $(selectedChecker).removeClass("selected");
            }
            selectedChecker = this;
            $(this).addClass("selected");
        }
    });

    // Обработчик клика по клетке
    $(".Cell").on("click", function () {
        if (selectedChecker) {
            const selectedCell = $(this);
            const selectedPosition = selectedCell.css("grid-area");
            const checkerPosition = $(selectedChecker).css("grid-area");

            // Получаем координаты выбранной клетки
            const selectedCoords = selectedPosition.trim().split(" / ").map(Number);
            const selectedRow = selectedCoords[0];
            const selectedCol = selectedCoords[1];

            // Получаем координаты текущей клетки шашки
            const checkerCoords = checkerPosition.trim().split(" / ").map(Number);
            const checkerRow = checkerCoords[0];
            const checkerCol = checkerCoords[1];

            // Вычисляем разницу координат
            const rowDiff = Math.abs(selectedRow - checkerRow);
            const colDiff = Math.abs(selectedCol - checkerCol);

            // Проверка на перепрыгивание (разница по строкам и столбцам должна быть 2)
            if (rowDiff === 2 && colDiff === 2) {
                // Находим координаты промежуточной клетки
                const middleRow = (selectedRow + checkerRow) / 2;
                const middleCol = (selectedCol + checkerCol) / 2;

                const middleCell = $(".Cell").filter(function () {
                    return $(this)
                        .css("grid-area")
                        .trim() === `${middleRow} / ${middleCol} / ${middleRow} / ${middleCol}`;
                });

                // Проверяем, есть ли шашка в средней клетке и если она противоположного цвета
                if (middleCell.length && middleCell.find(".checker").length) {
                    const middleChecker = middleCell.find(".checker");
                    const currentCheckerColor = $(selectedChecker).hasClass("black") ? "black" : "white";
                    const middleCheckerColor = middleChecker.hasClass("black") ? "black" : "white";

                    if (currentCheckerColor !== middleCheckerColor) {
                        // Уничтожаем промежуточную шашку
                        middleChecker.remove();

                        // Перемещаем выбранную шашку
                        $(selectedChecker).css("grid-area", selectedPosition);

                        // Снимаем выделение
                        $(selectedChecker).removeClass("selected");
                        selectedChecker = null;
                    }
                }
            }
            // Простое движение (разница по строкам и столбцам равна 1)
            else if (rowDiff === 1 && colDiff === 1) {
                $(selectedChecker).css("grid-area", selectedPosition);

                // Снимаем выделение
                $(selectedChecker).removeClass("selected");
                selectedChecker = null;
            }
        }
    });
});
