class GameModel {
    constructor() {
        this._board = {
            _checkers: [], // Массив шашек
            moveChecker: (oldRow, oldColumn, newRow, newColumn) => {
                const checker = this._board._checkers.find(
                    (c) => c._row === oldRow && c._column === oldColumn
                );
                if (checker) {
                    checker._row = newRow;
                    checker._column = newColumn;
                }
            },
        };
    }
}

export default GameModel;
