import React, { useState } from 'react';
import EnemyInfo from './Checkers/EnemyInfo.js';
import Game from './Checkers/Game.js';
import PlayerInfo from './Checkers/PlayerInfo.js';
import './Checkerss.css';

function Checkers({ GameModel }) {
    // Состояние для управления шашками
    const [checkers, setCheckers] = useState(GameModel._board._checkers);
    const [selectedChecker, setSelectedChecker] = useState(null);

    // Функция для обработки клика по шашке
    const handleCheckerClick = (row, column) => {
        setSelectedChecker({ row, column });
    };

    // Функция для обработки клика по клетке
    const handleCellClick = (newRow, newColumn) => {
        if (selectedChecker) {
            // Обновляем модель и состояние
            GameModel.moveChecker(
                selectedChecker.row,
                selectedChecker.column,
                newRow,
                newColumn
            );
            setCheckers([...GameModel._board._checkers]); // Обновляем состояние шашек
            setSelectedChecker(null); // Сбрасываем выбранную шашку
        }
    };

    return (
        <div className="Checkers">
            <EnemyInfo />
            {/* Передаём функции и данные в компонент Game */}
            <Game
                GameModel={GameModel}
                checkers={checkers}
                onCheckerClick={handleCheckerClick}
                onCellClick={handleCellClick}
            />
            <PlayerInfo />
        </div>
    );
}

export default Checkers;
