$(document).ready(function () {
    let selectedChecker = null;  // Для отслеживания выбранной шашки

    // При клике на шашку
    $('.checker').click(function () {
        if (selectedChecker) {
            // Перемещение шашки в выбранную клетку
            $(this).append(selectedChecker);
            selectedChecker = null;
        } else {
            // Сохраняем ссылку на выбранную шашку
            selectedChecker = $(this);
            selectedChecker.addClass('selected');  // Добавляем стиль выделения
        }
    });
});
