import "./Board.css";
import BackgroundCellGenerator from "./Board/BackgroundCellGenerator";
import GameModelCheckerGenerator from "./Board/GameModelCheckerGenerator";
import { useState } from "react";

function Board({ GameModel }) {
    const [checkers, setCheckers] = useState(GameModel._board._checkers); // Состояние шашек
    const [selectedChecker, setSelectedChecker] = useState(null); // Выбранная шашка

    // Обработчик клика по шашке
    const handleCheckerClick = (row, column) => {
        setSelectedChecker({ row, column });
    };

    // Обработчик клика по клетке
    const handleCellClick = (row, column) => {
        if (selectedChecker) {
            // Обновляем координаты выбранной шашки
            GameModel.moveChecker(selectedChecker.row, selectedChecker.column, row, column);
            setCheckers([...GameModel._board._checkers]); // Обновляем состояние шашек
            setSelectedChecker(null); // Снимаем выбор
        }
    };

    return (
        <div className="Board">
            {/* Генерация шашек */}
            <GameModelCheckerGenerator
                GameModel={GameModel}
                onCheckerClick={handleCheckerClick} // Передаем обработчик
            />

            {/* Генерация клеток */}
            <BackgroundCellGenerator onCellClick={handleCellClick} />
        </div>
    );
}

export default Board;
