// Получаем все шашки
const checkers = document.querySelectorAll('.checker');

// Обрабатываем событие начала перетаскивания
checkers.forEach(checker => {
    checker.addEventListener('dragstart', (event) => {
        // Добавляем класс "dragging" для изменения стиля
        event.target.classList.add('dragging');
    });

    checker.addEventListener('dragend', (event) => {
        // Убираем класс "dragging" после завершения перетаскивания
        event.target.classList.remove('dragging');
    });
});

// Обрабатываем перетаскивание по ячейкам поля
const cells = document.querySelectorAll('.cell');

cells.forEach(cell => {
    cell.addEventListener('dragover', (event) => {
        event.preventDefault();  // Разрешаем перетаскивание в ячейку
    });

    cell.addEventListener('drop', (event) => {
        event.preventDefault();
        const draggedChecker = document.querySelector('.dragging');
        if (draggedChecker) {
            // Перемещаем шашку в новую ячейку
            cell.appendChild(draggedChecker);
        }
    });
});
