// Найдем шашку и доску
const checker = document.getElementById("checker");
const board = document.getElementById("board");

// Обработчик перемещения
function moveChecker(row, column) {
    // Изменяем позицию шашки через grid-area
    checker.style.gridArea = `${row} / ${column}`;
}

// Клик по доске для перемещения
board.addEventListener("click", (event) => {
    // Получаем координаты клика относительно доски
    const rect = board.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Вычисляем строку и столбец, куда кликнули
    const row = Math.ceil(y / 50); // Размер клетки — 50px
    const column = Math.ceil(x / 50);

    // Переместим шашку
    //moveChecker(row, column);
    console.log(`ROW : ${row}, COL : ${column}`);
});
