$(document).ready(function() {
    // Функция для перемещения шашки
    let selectedChecker = null;

    $(".checker").on("click", function() {
        // Если шашка уже выбрана, то отменяем выбор
        if (selectedChecker === this) {
            selectedChecker = null;
            $(this).removeClass("selected");
        } else {
            // Если шашка не выбрана, то выбираем ее
            if (selectedChecker) {
                $(selectedChecker).removeClass("selected");
            }
            selectedChecker = this;
            $(this).addClass("selected");
        }
    });

    $(".Cell").on("click", function() {
        // Если шашка выбрана, перемещаем ее в выбранную клетку
        if (selectedChecker) {
            // Получаем стиль для текущей клетки
            const newStyle = $(this).attr("style");

            // Перемещаем шашку в новую клетку
            $(selectedChecker).css("grid-area", $(this).css("grid-area"));

            // Убираем класс выделения
            $(selectedChecker).removeClass("selected");
            selectedChecker = null;
        }
    });
});
